var palavras = require('./palavras.json');
var numeros = require('./numeros.json');

module.exports.generateUsername = function (separator, length) {
	if(separator==undefined)separator="";
	var palavra = palavras[Math.floor(Math.random()*palavras.length)];
	var numero = numeros[Math.floor(Math.random()*numeros.length)];

	var result = palavra + separator + numero;
	
	if (length) {
		result = result.substring(0, length);
	}
	return result
};
